import "./Expense.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card" //.. means two step up

const  Expense = (props) => {
  return (
    <Card className="expenses">
     <ExpenseItem
        name={props.item[0].name}
        amount={props.item[0].amount}
        date={props.item[0].date}
      ></ExpenseItem>
      {/* we use another components in another file like this first letter is capital letter if we want to use this.    */}
      <ExpenseItem
    //   these are the attributes ie name amount date
        name={props.item[1].name}
        amount={props.item[1].amount}
        date={props.item[1].date}
      ></ExpenseItem>
      <ExpenseItem
        name={props.item[2].name}
        amount={props.item[2].amount}
        date={props.item[2].date}
      ></ExpenseItem>
    </Card>
    //exporting and importing data with the help of props.
    //props is automatically hit on the app.js
  );
}

export default Expense;

//what is lifting state up?
//jaise yhan do siblings component aps mae comm. nhi kr skte jaise yhan newexpense or expense 
//toh us comm. ko achieve krne k liye hum us closest component ka sahara lete hai jo direct 
//yah indert dono se baat krra ho aise mae yhan app ka sahara lia hmne phle data app mae aya 
//kyuki app render kr rha hai expense ko toh wo data expense tk bhi aa jaiga.
