import ExpenseDate from "./ExpenseDate";
import "./ExpenseItem.css";
import Expense from "./Expense"

function ExpenseItem(props) { // props access the propery of app.js 

  return (
    <div className="expense-item">{/* this is the root element i.e only one in one component */}
   
     <ExpenseDate date={props.date}></ExpenseDate>
     {/* because date come from app to expensedate and expensedate to here in expenseitem thats why we use attribute concept here. */}
      <div className="expense-item__description">
        <h2>{props.name}</h2>
        <div className="expense-item__price">RS{props.amount}</div>
      </div>
    </div>
  );
}

export default ExpenseItem;

//shift+alt+f for format the document

